import java.util.Scanner;  // Import Scanner class for user input

public class inputSwitch {

    public static void main(String[] args) {

        Scanner inputSwitchData = new Scanner(System.in); // Create Scanner object

        System.out.println("Enter a number (1-3): "); // Prompt user for input

        int inputData = inputSwitchData.nextInt(); // Read user input

        // Switch case to check user input
        switch (inputData) {
            case 1:
                System.out.println("You Select System 1");
                break;

            case 2:
                System.out.println("You Select System 2");
                break;

            case 3:
                System.out.println("You Select System 3");
                break;

            default:
                System.out.println("Invalid Choice"); // Handle invalid input
        }

        inputSwitchData.close(); // Close Scanner object
    }
}

