public class ChangeElementArray {

    // Instance variable (array)  
    String[] cars = {"Toyota"}; 

    public static void main(String[] args) {

        // Local array  
        String[] fruit = {"Apple"};  

        // Modifying local array  
        fruit[0] = "Mango";  

        // Creating an object to access instance variable  
        ChangeElementArray changeArray = new ChangeElementArray();  

        // Modifying instance array  
        changeArray.cars[0] = "Tesla";  

        // Printing modified values  
        System.out.println(changeArray.cars[0]); // Output: Tesla  
        System.out.println(fruit[0]); // Output: Mango  
    }
}

